body.script:
function phgcookies_onload(force) {

         if(typeof force != "undefined" && force == "true") {
         append_url = "?force=true";
         } else {
         append_url = "";
         }

         if(phgcookies_hasexistingprefs() == false || (typeof force != "undefined" && force == "true")) {
         jQuery.ajax("https://publicholidays.ro/wp-json/phgcookies/v1/check/" + append_url, {cache:
         false}).done(function(resp) {
         document.phgcookies_languages = resp.languages;
         document.phgcookies_language = resp.language_default;
         phgcookies_generatepopup(resp);
         });
         } else {
         phgcookies_execprefs();
         }

         return void(0);
         }
         function phgcookies_generatepopup(resp) {
         if(resp.request_optin == true) {
         var popup = jQuery("#phg_cookies_modal");

         // Empty language dropdown
         jQuery("select.phgcookies_language_select", popup).html("");

         // Populate language dropdown
         for(var i = 0; i< document.phgcookies_languages.length; i++) {
         jQuery("select.phgcookies_language_select", popup).append("
         "+document.phgcookies_languages[i].languageName+"");
         }

         phgcookies_languageset();

         // Pre-fill checkboxes based on previous prefs
         if(phgcookies_hasconsent("statistics") != null) {
         jQuery(".phgcookies_statistics", popup).prop("checked", phgcookies_hasconsent("statistics"));
         } else {
         jQuery(".phgcookies_statistics", popup).prop("checked", true);
         }
         if(phgcookies_hasconsent("marketing") != null) {
         jQuery(".phgcookies_marketing", popup).prop("checked", phgcookies_hasconsent("marketing"));
         } else {
         jQuery(".phgcookies_marketing", popup).prop("checked", false);
         }

         // Show the Zurb Foundation 'Reveal' popup
         popup.foundation("open");
         } else {
         if(!phgcookies_hasexistingprefs()) {
         // These users aren't required to opt-in, so we just consent them to everything
         var name = "phgcookies";
         var value = {
         "necessary": true,
         "statistics": true,
         "marketing": true,
         "last_set": Date.now(),
         "auto_optin": true
         };
         value = JSON.stringify(value);

         var date = new Date();
         date.setTime(date.getTime() + (365 *24*60*60*1000));
         var expires = "; expires=" + date.toUTCString();

         document.cookie = name + "=" + (value || "") + expires + "; path=/";
         }

         phgcookies_execprefs();
         }
         }
         function phgcookies_hasexistingprefs() {
         // Returns true/false if existing preferences have been set

         // Returns the cookie pref value, or null if it hasn't been set
         cookie_settings = (document.cookie.match(/^(?:.*;)?\s*phgcookies\s*=\s*([^;]+)(?:.*)?$/)||[,null])[1];

         if(cookie_settings == null) {
         return false;
         }

         return true;
         }
         function phgcookies_hasconsent(level) {
         if(!phgcookies_hasexistingprefs()) {
         return false;
         }

         // Returns the cookie pref value, or null if it hasn't been set
         cookie_settings = (document.cookie.match(/^(?:.*;)?\s*phgcookies\s*=\s*([^;]+)(?:.*)?$/)||[,null])[1];

         // Parse the JSON
         cookie_settings = JSON.parse(cookie_settings);

         if(typeof cookie_settings[level] != "undefined" && cookie_settings[level] == true) {
         return true;
         } else if(typeof cookie_settings[level] != "undefined" && cookie_settings[level] == false) {
         return false;
         }

         return null;
         }
         function phgcookies_opinkey() {
         if(!phgcookies_hasexistingprefs()) {
         return false;
         }

         // Returns the cookie pref value, or null if it hasn't been set
         cookie_settings = (document.cookie.match(/^(?:.*;)?\s*phgcookies\s*=\s*([^;]+)(?:.*)?$/)||[,null])[1];

         // Parse the JSON
         cookie_settings = JSON.parse(cookie_settings);

         if(typeof cookie_settings["key"] != "undefined" && typeof cookie_settings["id"] != "undefined") {
         return {"id": cookie_settings["id"], "key": cookie_settings["key"]};
         }

         return null;
         }
         function phgcookies_opinkeyset(key, id) {
         if(!phgcookies_hasexistingprefs()) {
         return false;
         }

         // Returns the cookie pref value, or null if it hasn't been set
         cookie_settings = (document.cookie.match(/^(?:.*;)?\s*phgcookies\s*=\s*([^;]+)(?:.*)?$/)||[,null])[1];

         // Parse the JSON
         cookie_settings = JSON.parse(cookie_settings);

         // Add the new fields
         cookie_settings["key"] = key;
         cookie_settings["id"] = id;

         // Turn it back into JSON
         var value = JSON.stringify(cookie_settings);

         var date = new Date();
         date.setTime(date.getTime() + (365 *24*60*60*1000));
         var expires = "; expires=" + date.toUTCString();

         document.cookie = "phgcookies" + "=" + (value || "") + expires + "; path=/";

         return null;
         }
         function phgcookies_saveprefs(mode) {
         // Save new cookie preferences

         var popup = jQuery("#phg_cookies_modal");

         if(typeof mode != "undefined" && mode == "all") {
         var statistics = true;
         var marketing = true;
         } else {
         var statistics = jQuery(".phgcookies_statistics", popup).is(":checked");
         var marketing = jQuery(".phgcookies_marketing", popup).is(":checked");
         }

         var name = "phgcookies";
         var value = {
         "necessary": true,
         "statistics": statistics,
         "marketing": marketing,
         "last_set": Date.now(),
         "auto_optin": false
         };

         optinKey = phgcookies_opinkey();
         if(optinKey !== null) {
         value["key"] = optinKey["key"];
         value["id"] = optinKey["id"];
         }

         value = JSON.stringify(value);

         var date = new Date();
         date.setTime(date.getTime() + (365 *24*60*60*1000));
         var expires = "; expires=" + date.toUTCString();

         document.cookie = name + "=" + (value || "") + expires + "; path=/";

         popup.foundation("close");
         phgcookies_execprefs();

         // Prepare to send the data back to the server for logging
         ajaxData = {
         "preference_statistics": statistics,
         "preference_marketing": marketing,
         };

         if(optinKey !== null) {
         ajaxData["key"] = optinKey["key"];
         ajaxData["id"] = optinKey["id"];
         }

         // Log the opt-in on the server
         jQuery.ajax({
         type: "POST",
         url: "https://publicholidays.ro/wp-json/phgcookies/v1/optin/",
         data: ajaxData,
         success: function(resp) {
         if(typeof resp["id"] != "undefined" && typeof resp["key"] != "undefined") {
         phgcookies_opinkeyset(resp["key"], resp["id"])
         }
         },
         dataType: "json"
         });

         // This function doesn't need to return anything - except false to stop the hyperlink
         return void(0);

         }
         function phgcookies_adsenseload() {
         // Later on, allow loading of ads
         if(phgcookies_hasconsent("marketing") == false) {
         (adsbygoogle=window.adsbygoogle||[]).requestNonPersonalizedAds=1;
         } else {
         (adsbygoogle=window.adsbygoogle||[]).requestNonPersonalizedAds=0;
         }

         (adsbygoogle=window.adsbygoogle||[]).pauseAdRequests=0;
         }
         function phgcookies_execprefs() {
         // Execute existing cookie preferences

         jQuery("script").each(function(index) {
         consent_type = jQuery(this).attr("data-cookieconsent");
         data_type = jQuery(this).attr("type");

         if(typeof consent_type != "undefined" && (phgcookies_hasconsent(consent_type) ||
         phgcookies_hasconsent(consent_type) == null) && typeof data_type != "undefined" && data_type ==
         "text/plain") {
         jQuery(this).attr("type", "text/javascript");
         try {
         jQuery(this).after( jQuery(this)[0].outerHTML );
         } catch(error) {
         console.log(error);
         console.log(this);
         }
         jQuery(this).remove();
         }
         });

         phgcookies_adsenseload();
         }
         function phgcookies_edit() {
         // Show the 2nd section with granular settings

         var popup = jQuery("#phg_cookies_modal");
         jQuery(".phgcookies_expand", popup).show();
         jQuery(".phgcookies_label_edit", popup).css("visibility", "hidden");
         jQuery(".phgcookies_label_okay", popup).css("visibility", "hidden");
         }
         function phgcookies_languageset() {
         // Called when the user changes the language drop-down

         var popup = jQuery("#phg_cookies_modal");
         var newDefaultLang = jQuery("select.phgcookies_language_select", popup).val();

         if(newDefaultLang !== "" && typeof newDefaultLang != "undefined") {
         document.phgcookies_language = newDefaultLang;
         }

         var language = document.phgcookies_languages[document.phgcookies_language];

         // Top description:
         jQuery(".phgcookies_text_description", popup).html(language.text_description);

         // Necessary/Statistics/Marketing
         jQuery(".phgcookies_label_necessary", popup).html(language.label_necessary);
         jQuery(".phgcookies_label_statistics", popup).html(language.label_statistics);
         jQuery(".phgcookies_label_marketing", popup).html(language.label_marketing);
         jQuery(".phgcookies_text_necessary", popup).html(language.text_necessary);
         jQuery(".phgcookies_text_statistics", popup).html(language.text_statistics);
         jQuery(".phgcookies_text_marketing", popup).html(language.text_marketing);

         // Button labels:
         jQuery(".phgcookies_label_edit", popup).html(language.label_edit);
         jQuery(".phgcookies_label_okay", popup).html(language.label_okay);
         jQuery(".phgcookies_label_acceptall", popup).html(language.label_acceptall);
         jQuery(".phgcookies_label_save", popup).html(language.label_save);
         jQuery(".phgcookies_label_or", popup).html(language.label_or);

         // Footer label:
         jQuery(".phgcookies_text_footer", popup).html(language.text_footer);

         }

         jQuery(document).ready(function(){
         phgcookies_onload();
         });

